const initialState = {
    links: null,
    err: ''
}

export default function linkReducer(state = initialState, action){
    switch(action.type){
        case 'GET_LINKS':
            return {
                ...state, 
                links: action.payload
            }
        case 'ERRORS_FOUND':
            return{
                ...state,
                links: null,
                err: action.payload
            }
        default: 
            return state 
    }
}
