const initialState = {
  user_id: -1,
  err: '',
  admin: false,
  superUser: false,
  lastPage: null,
  lastScript: null,
  lastMentee: null
}



export default function userReducer(state = initialState, action) {
    /*
    This reducer will do one thing (Based on two separate actions):
    1) Loads up only the pages pertaining to the script chosen
    
    */
    switch (action.type) {
      //Number 1 from above is accomplished through LOADING and SUCCESS
      case 'ERRORS_FOUND':
        return {
          ...state,
          err: action.payload,
          user_id: -1
        }
      case 'USER_SUCCESS':
        if (action.payload.super_user === true){
          return{
            ...state,
            err: '',
            user_id: action.payload.id,
            admin: true,
            superUser: true,
            lastPage: action.payload.last_page_number,
            lastScript: action.payload.last_script_number,
            lastMentee: action.payload.last_mentee_id
          }
        }
        else if (action.payload.admin === true){
          return{
            ...state,
            err: '',
            user_id: action.payload.id,
            admin: true,
            lastPage: action.payload.last_page_number,
            lastScript: action.payload.last_script_number
          }
        }
        else{
          return{
            ...state,
            err: '',
            user_id: action.payload.id,
            lastPage: action.payload.last_page_number,
            lastScript: action.payload.last_script_number
          }
        }
        
      case 'CHANGE_PAGE':
        return{
          ...state,
          lastPage: action.payload
        }
      
      case 'CHANGE_SCRIPT':
        return{
          ...state,
          lastScript: action.payload
        }
      case 'LOGOUT':
        return{
          ...state,
          user_id: -1
        }
      default:
        // If the reducer doesn't care about this action type,
        // return the existing state unchanged
        return state
    }
  }
