import { Component } from 'react';
import { connect } from 'react-redux';

import { patchTexts, postTexts } from '../../../../actions/text';

class Page7 extends Component {

    handleChange = (event) => {
        const object_outcome = this.getObject(event.target.id)
        object_outcome === "" ? 
        this.props.postTexts({value: event.target.value, id_tag: event.target.id, mentee_id: this.props.mentee_id, script: this.props.script})
        :
        this.props.patchTexts({value: event.target.value, id_tag: event.target.id, id: object_outcome.id, mentee_id: this.props.mentee_id, script: this.props.script})
    
    }
    
    getObject = (current_id_tag) => {
        //Returns the object that has the specific id_tag
        let current_text = this.props.texts.find(text_item => {return text_item.id_tag === current_id_tag})
        return current_text ? current_text : ""
    }
    
    getValue = (current_id_tag) => {
        //Same as getObject but instead it returns the value
        let current_text_for_value = this.props.texts.find(text_item => {return text_item.id_tag === current_id_tag})
        return current_text_for_value ? current_text_for_value.value : ""
    }

    render() {
        return (
            <div className="sheet">
                 <h1 className="bold center">Practicing</h1>
                <div className="left">
                    <div className='container_for_small_margin'>
                        <p>Is there any part of your coping strategy that you want to practice together?</p>
                    </div>

                    <div id="instruction_box_number_1_page_7_script_13" className="custom_svg demo_box container_for_small_margin">
                        <p className="top_line_in_instruction_box">If your mentee says yes, practice with them for 10-15 minutes</p>
                    </div>
                    
                    <div className='container_for_small_margin'>
                        <p>Is there anything you need to help you use this strategy outside of mentoring? </p>
                    </div>

                    <div id="instruction_box_number_1_page_7_script_13" className="custom_svg demo_box container_for_small_margin">
                        <p className="top_line_in_instruction_box">
                            Talk with your mentee about the help they need.<br/>
                            For example, you could help them think about setting reminders or using their toolkit. 
                        </p>
                    </div>
                </div>
            </div>
        )
    }
    
}

const mapStateToProps = state => {
    return{
        texts: state.texts.curatedTextsFromCurrentScript,
        mentee_id: state.mentees.current_mentee_id,
        script: state.texts.currentScript
    }
}

const mapDispatchToProps = dispatch => {
    return{
        patchTexts: (text_data) => dispatch(patchTexts(text_data)),
        postTexts: (text_data) => dispatch(postTexts(text_data))

    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Page7);
