import { IMAGE_LOADING, IMAGE_SUCCESS } from '../actions/body';

const initialState = {
    loading: false,
    imageClicked: false,

}

export default function commentReducer(state = initialState, action){
    switch(action.type){
        case IMAGE_LOADING:
            return {
                ...state,
                loading: true
    
            }

        case IMAGE_SUCCESS:
            return {
                ...state,
                loading: false
            }

        case 'TOGGLE_BODY':
            return {
                ...state,
                imageClicked: !state.imageClicked
            }
        
    
        default: 
            return state 
    }
}
