import { connect } from 'react-redux';

import { patchTexts, postTexts } from '../../../../actions/text';

const Page5 = (props) => {

    const handleChange = (event) => {
        const object_outcome = getObject(event.target.id)
        object_outcome === "" ? 
        props.postTexts({value: event.target.value, id_tag: event.target.id, mentee_id: props.props.mentee_id, script: props.script})
        :
        props.patchTexts({value: event.target.value, id_tag: event.target.id, id: object_outcome.id, mentee_id: props.props.mentee_id, script: props.script})
    
    }
    
    const getObject = (current_id_tag) => {
        //Returns the object that has the specific id_tag
        let current_text = props.texts.find(text_item => {return text_item.id_tag === current_id_tag})
        return current_text ? current_text : ""
    }
    
    const getValue = (current_id_tag) => {
        //Same as getObject but instead it returns the value
        let current_text_for_value = props.texts.find(text_item => {return text_item.id_tag === current_id_tag})
        return current_text_for_value ? current_text_for_value.value : ""
    }

    return (
        <div className="sheet">
            <h1 className="bold center">Reviewing your mentee's coping strategy toolkit</h1>
            <div className="left container_for_small_margin">
                <p>
                    You have been practicing coping straegies for almost three months.<br/>
                    Let's think about each of your coping strategies. <br/>
                    We are going to go through a worksheet to think about how each of your coping strategies is going. <br/>
                    In the last few sessions, we will work together on a plan to make sure you can use your coping strategies when mentoring is over.
                </p>
                <div id="instruction_box_number_1_page_5_script_14" className="custom_svg demo_box container_for_medium_margin">
                    <p className="top_line_in_instruction_box"> 
                        Send your mentee this link: <textarea onChange={event => handleChange(event)} id="text_box_number_1_page_5_script_14" defaultValue={getValue("text_box_number_1_page_5_script_14")} /><br/>
                        Ask your mentee if they want to share their screen or share yours.<br/>
                        Talk through the worksheet together. <br/>
                        If you think it would help your mentee, you can use the solution website together (link: <a href="https://sites.google.com/view/peer-mentoring-solutions">Solutions Link</a>).<br/>
                        When you are done, stop screen sharing.
                    </p>
                </div>
            </div>   
        </div>
    )
}


const mapStateToProps = state => {
    return{
        texts: state.texts.curatedTextsFromCurrentScript,
        mentee_id: state.mentees.current_mentee_id,
        script: state.texts.currentScript
    }
}

const mapDispatchToProps = dispatch => {
    return{
        patchTexts: (text_data) => dispatch(patchTexts(text_data)),
        postTexts: (text_data) => dispatch(postTexts(text_data))

    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Page5);
