import React, { Component } from 'react';
import allEars from "../../pictures/allears.png";
import pause from "../../pictures/pause.png";
import stopShare from "../../pictures/stopshare.png";
import shareScreen from "../../pictures/sharescreen.png";
import axios from 'axios';


class Page6 extends Component {
    
    state= {
        text: {}
    }
    
    handleScroll=()=>{
        window.scroll({top:0,behavior:'smooth'})
    
    }
    componentDidMount() {
        this.handleScroll()
        axios.get("http://localhost:3001/texts")
        .then(res => {
            const texts = res.data;
            for (const txt of texts){
                if (txt.sheet === "2"){
                    this.setState({
                        text: {...this.state.text, [txt.id_tag]: txt}
                    })
                }
            }
              
    })
}

    handleChange = (event) => {
        this.setState({text: {...this.state.text, [event.target.id]: {value: event.target.value, id_tag: event.target.id}}})
        if (event.target.id in this.state.text){
            axios.patch(`http://localhost:3001/texts/${this.state.text[event.target.id].id}`, {value: event.target.value, id_tag: event.target.id, sheet: "2"})
    }
        else {
            axios.post("http://localhost:3001/texts", {value: event.target.value, id_tag: event.target.id, sheet: "2" })
        }
    }

    getValue = (id) => {
        for (const i in this.state.text){
            if (this.state.text[i].id_tag === id){
                return this.state.text[i].value;
            }
        }
        return ""
    }

    render() {
        return (
            <div className="sheet">
                <div id="page6" className="left">
                    <h1 id="p6title" className="bold center">Mental Health Worksheet</h1>
                    <br /><br />
                    <h2 >What does mental health mean to you? </h2>
                    <div id="pics">
                        <img className="pause" src={pause} alt="pause" />
                        <img className="allEars" src={allEars} alt="All ears" />
                    </div>
                    <h2>Letâ€™s work together to find pictures that describe mental health. You can do a search online or share pictures you have taken. Or, you can send me an emoji or a GIF to show me what mental health means to you. </h2>
                    <br /> <br />
                    <h2>There is no right or wrong way to do this. I can help you if you want.</h2>
                    <br /> <br />
                    <h2>Here is an example. </h2>
                    <br /><br />
                    <div className="custom_svg demo_box" id="inst_1_6">
                        <h2 className="in_inst">Share your screen to show your picture</h2>
                        <br/><br/>
                        <h2 className="in_inst">Link to your picture <textarea onChange={event => this.handleChange(event)} id="tad_1_6" className="textarea" defaultValue={this.getValue("tad_1_6")} /></h2>
                    </div>
                    <img src={shareScreen} alt="Share Screen" id="shareScreen6" />
                    <h2>I picked this picture because <textarea onChange={event => this.handleChange(event)} id="tad_2_6" className="textarea" defaultValue={this.getValue("tad_2_6")} /></h2>
                    <br/>
                    <img src={stopShare} alt="Stop share" id="stopShare6" />
                </div>
            </div>
        )
    }


}
export default Page6;

